def consume_socket_content(sock, timeout=0.5):
    chunks = 65536
    content = b""

    while True:
        more_to_read = select.select([sock], [], [], timeout)[0]
        if not more_to_read:
            break

        new_content = sock.recv(chunks)
        if not new_content:
            break

        content += new_content

    return content

def text_response_server(cls, text, request_timeout=0.5, **kwargs):
    def text_response_handler(sock):
        request_content = consume_socket_content(sock, timeout=request_timeout)
        sock.send(text.encode("utf-8"))

        return request_content

    return Server(text_response_handler, **kwargs)

def _close_server_sock_ignore_errors(self):
    try:
        self.server_sock.close()
    except OSError:
        pass

def _handle_requests(self):
    for _ in range(self.requests_to_handle):
        sock = self._accept_connection()
        if not sock:
            break

        handler_result = self.handler(sock)

        self.handler_results.append(handler_result)
        sock.close()

def _accept_connection(self):
    try:
        ready, _, _ = select.select(
            [self.server_sock], [], [], self.WAIT_EVENT_TIMEOUT
        )
        if not ready:
            return None

        return self.server_sock.accept()[0]
    except OSError:
        return None

def __enter__(self):
    self.start()
    if not self.ready_event.wait(self.WAIT_EVENT_TIMEOUT):
        raise RuntimeError("Timeout waiting for server to be ready.")
    return self.host, self.port

def __exit__(self, exc_type, exc_value, traceback):
    if exc_type is None:
        self.stop_event.wait(self.WAIT_EVENT_TIMEOUT)
    else:
        if self.wait_to_close_event:
            # avoid server from waiting for event timeouts
            # if an exception is found in the main thread
            self.wait_to_close_event.set()

    # ensure server thread doesn't get stuck waiting for connections
    self._close_server_sock_ignore_errors()
    self.join()
    return False  # allow exceptions to propagate

def override_environ(**kwargs):
    save_env = dict(os.environ)
    for key, value in kwargs.items():
        if value is None:
            del os.environ[key]
        else:
            os.environ[key] = value
    try:
        yield
    finally:
        os.environ.clear()
        os.environ.update(save_env)
